    {
        "number": 4,
        "max_score": 10,
        "quest_id": 26,
        "text": "A random variable $X$ is uniformly distributed between 2 and 8. What is the probability that $X$ is less than 5?",
        "answers": [
            {
                "is_correct": true,
                "text": "0.5",
                "reason": "Correct. $P(X < 5) = \\frac{5 - 2}{8 - 2} = \\frac{3}{6} = 0.5$."
            },
            {
                "is_correct": false,
                "text": "0.25",
                "reason": "Incorrect. This would be correct if the interval were larger."
            },
            {
                "is_correct": false,
                "text": "0.333",
                "reason": "Incorrect. This corresponds to $P(X < 4)$."
            },
            {
                "is_correct": false,
                "text": "0.6",
                "reason": "Incorrect. This is not consistent with the uniform distribution over [2, 8]."
            }
        ]
    },
    {
        "number": 5,
        "max_score": 10,
        "quest_id": 26,
        "text": "The heights of adult men are normally distributed with a mean of 70 inches and a standard deviation of 3 inches. Which of the following statements are true? (Select all that apply.)",
        "answers": [
            {
                "is_correct": true,
                "text": "Approximately 68% of men are between 67 and 73 inches tall.",
                "reason": "Correct. This represents one standard deviation from the mean in a normal distribution."
            },
            {
                "is_correct": false,
                "text": "Approximately 95% of men are taller than 64 inches.",
                "reason": "Incorrect. Approximately 95% are between 64 and 76 inches (within two standard deviations)."
            },
            {
                "is_correct": true,
                "text": "The probability that a randomly selected man is taller than 73 inches is 0.1587.",
                "reason": "Correct. The z-score is 1, so $P(Z > 1) \\approx 0.1587$."
            },
            {
                "is_correct": false,
                "text": "The probability that a randomly selected man is shorter than 67 inches is 0.8413.",
                "reason": "Incorrect. $P(Z < -1) = 0.1587$, so the probability is 0.1587, not 0.8413."
            }
        ]
    }
